import React, { useState, useContext } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";
import OrderForm from "./components/Orders/OrderForm";
import Feedback from "./components/Feedback/Feedback";
import CartContext from "./components/store/cart-context";

function App() {
  const cartContext = useContext(CartContext);
  const [showingCart, setShowingCart] = useState(false);
  const [showingOrders, setShowingOrders] = useState(false);
  const [showingFeedback, setShowingFeedback] = useState(false);

  function showCart() {
    setShowingCart(true);
  }

  function hideCart() {
    setShowingCart(false);
    setShowingFeedback(false);
  }

  function showOrders() {
    setShowingOrders(true);
  }

  function hideOrders() {
    setShowingOrders(false);
    setShowingFeedback(true);
  }

  function closingOrderForm() {
    setShowingOrders(false);
  }

  function closeFeedback() {
    cartContext.clearCart();
    console.log("submit button clicked");
    setShowingFeedback(false);
  }

  function goBack() {
    setShowingOrders(false);
    setShowingCart(true);
  }

  return (
    <Router>
      <Switch>
        <Route path="/" exact>
          <React.Fragment>
            {showingOrders && (
              <OrderForm
                toCloseOrderFrom={closingOrderForm}
                toContinueHandler={hideOrders}
                toGoBack={goBack}
              />
            )}
            {showingFeedback && (
              <Feedback onSubmit={closeFeedback} toCloseCart={hideCart} />
            )}
            {showingCart && (
              <Cart toCloseCart={hideCart} toShowOrders={showOrders} />
            )}
            <Header toShowCart={showCart} />
            <main>
              <Meals />
            </main>
          </React.Fragment>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
