function LoginPage() {
  const initialFromIk = {
    usernameLogin: "",
    passwordLogin: "",
    username: "",
    password: "",
    confirmpassword: "",
    email: "",
    name: "",
  };
  const formik = useFormik({
    initialValues: initialFromIk,
    onSubmit: (values) => {
      const formdata = {
        usernameLogin: values.usernameLogin,
        passwordLogin: values.passwordLogin,
        username: values.username,
        password: values.password,
        confirmpassword: values.confirmpassword,
        email: values.email,
        name: values.name,
      };
      console.log(formdata);
      formik.resetForm();
    },
  });

  const [signup, setSignUp] = useState(false);
  return (
    <>
      <Header />
      {/* {signup && <SignUpComponent />}
      {!signup && <LoginComponent className="loginComponent" />} */}
      {signup ? (
        <form onSubmit={signUpFromik.handleSubmit}>
          <MDBContainer
            style={{
              fontFamily: "'Overpass', sans-serif",
              backgroundColor: "transparent",
              boxShadow: "none",
            }}
          >
            <MDBRow className="d-flex justify-content-center align-items-center h-100">
              <MDBCol col="12">
                <MDBCard
                  className="text-white my-5 mx-auto"
                  style={{
                    borderRadius: "1rem",
                    maxWidth: "400px",
                    backgroundColor: "#282f33",
                  }}
                >
                  <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
                    <h4>WELCOME TO</h4>
                    <h2 className="fw-bold mb-2 text-uppercase">ReactBites</h2>

                    <p className="text-white-50 mb-5">
                      Login for a seamless experience
                    </p>

                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Enter username"
                      name="username"
                      id="username"
                      type="text"
                      size="lg"
                      value={signUpFromik.values.username}
                      onChange={signUpFromik.handleChange}
                      style={{ color: "white" }}
                    />
                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Enter name"
                      name="name"
                      id="name"
                      type="text"
                      size="lg"
                      value={signUpFromik.values.name}
                      onChange={signUpFromik.handleChange}
                      style={{ color: "white" }}
                    />
                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Enter Email"
                      name="email"
                      id="email"
                      type="email"
                      size="lg"
                      value={signUpFromik.values.email}
                      onChange={signUpFromik.handleChange}
                      style={{ color: "white" }}
                    />
                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Password?"
                      id="password"
                      name="password"
                      type="password"
                      size="lg"
                      value={signUpFromik.values.password}
                      onChange={signUpFromik.handleChange}
                      style={{ color: "white" }}
                    />
                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Confirm Passoword?"
                      id="confirmpassword"
                      name="confirmpassword"
                      type="password"
                      size="lg"
                      value={signUpFromik.values.confirmpassword}
                      onChange={signUpFromik.handleChange}
                      style={{ color: "white" }}
                    />

                    <MDBBtn
                      outline
                      className="mt-2 mx-2 px-5"
                      color="white"
                      size="lg"
                      type="submit"
                      onClick={() => {
                        navigate("/");
                      }}
                    >
                      SignUp
                    </MDBBtn>

                    <div>
                      <p className="mb-0 mt-4">
                        Already have an account?{" "}
                        <span
                          className="text-white-50 fw-bold"
                          style={{ cursor: "pointer" }}
                        >
                          Login
                        </span>
                      </p>
                    </div>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        </form>
      ) : (
        <form onSubmit={formik.handleSubmit}>
          <MDBContainer
            style={{
              fontFamily: "'Overpass', sans-serif",
              backgroundColor: "transparent",
              boxShadow: "none",
            }}
          >
            <MDBRow className="d-flex justify-content-center align-items-center h-100">
              <MDBCol col="12">
                <MDBCard
                  className="text-white my-5 mx-auto"
                  style={{
                    borderRadius: "1rem",
                    maxWidth: "400px",
                    backgroundColor: "#282f33",
                  }}
                >
                  <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
                    <h4>WELCOME TO</h4>
                    <h2 className="fw-bold mb-2 text-uppercase">ReactBites</h2>

                    <p className="text-white-50 mb-5">
                      Login for a seamless experience
                    </p>

                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Username"
                      name="usernameLogin"
                      id="usernameLogin"
                      type="text"
                      size="lg"
                      value={formik.values.usernameLogin}
                      onChange={formik.handleChange}
                      style={{ color: "white" }}
                    />
                    <MDBInput
                      wrapperClass="mb-4 mx-5 w-100"
                      labelClass="text-white"
                      label="Password"
                      id="passswordLogin"
                      name="passswordLogin"
                      type="password"
                      size="lg"
                      value={formik.values.passswordLogin}
                      onChange={formik.handleChange}
                      style={{ color: "white" }}
                    />

                    <MDBBtn
                      outline
                      className="mt-2 mx-2 px-5"
                      color="white"
                      size="lg"
                      type="submit"
                      onClick={() => {
                        navigate("/");
                      }}
                    >
                      Login
                    </MDBBtn>

                    <div>
                      <p className="mb-0 mt-4">
                        Don't have an account?{" "}
                        <span
                          className="text-white-50 fw-bold"
                          style={{ cursor: "pointer" }}
                        >
                          Sign Up
                        </span>
                      </p>
                    </div>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
        </form>
      )}
    </>
  );
}

export default LoginPage;
