import React, { useState, useEffect } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
} from "mdb-react-ui-kit";
import { useFormik } from "formik";
import { useNavigate } from "react-router-dom";

import "./cssFiles/loginComponent.css";
import Header from "../Layout/Header";

const initial = {
  usernameLogin: "",
  passwordLogin: "",
};

const signUpInitial = {
  username: "",
  password: "",
  confirmpassword: "",
  email: "",
  name: "",
};

function LoginComponent({ formik }) {
  const navigate = useNavigate();
  return (
    <form onSubmit={formik.handleSubmit}>
      <MDBContainer
        style={{
          fontFamily: "'Overpass', sans-serif",
          backgroundColor: "transparent",
          boxShadow: "none",
        }}
      >
        <MDBRow className="d-flex justify-content-center align-items-center h-100">
          <MDBCol col="12">
            <MDBCard
              className="text-white my-5 mx-auto"
              style={{
                borderRadius: "1rem",
                maxWidth: "400px",
                backgroundColor: "#282f33",
              }}
            >
              <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
                <h4>WELCOME TO</h4>
                <h2 className="fw-bold mb-2 text-uppercase">ReactBites</h2>

                <p className="text-white-50 mb-5">
                  Login for a seamless experience
                </p>

                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Username"
                  name="usernameLogin"
                  id="usernameLogin"
                  type="text"
                  size="lg"
                  value={formik.values.usernameLogin}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Password"
                  id="passwordLogin"
                  name="passwordLogin"
                  type="password"
                  size="lg"
                  value={formik.values.passwordLogin}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />

                <MDBBtn
                  outline
                  className="mt-2 mx-2 px-5"
                  color="white"
                  size="lg"
                  type="submit"
                  onClick={() => {
                    navigate("/");
                  }}
                >
                  Login
                </MDBBtn>

                <div>
                  <p className="mb-0 mt-4">
                    Don't have an account?{" "}
                    <span
                      className="text-white-50 fw-bold"
                      style={{ cursor: "pointer" }}
                    >
                      Sign Up
                    </span>
                  </p>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </form>
  );
}

function SignUpComponent({ formik }) {
  const navigate = useNavigate();
  return (
    <form onSubmit={formik.handleSubmit}>
      <MDBContainer
        style={{
          fontFamily: "'Overpass', sans-serif",
          backgroundColor: "transparent",
          boxShadow: "none",
        }}
      >
        <MDBRow className="d-flex justify-content-center align-items-center h-100">
          <MDBCol col="12">
            <MDBCard
              className="text-white my-5 mx-auto"
              style={{
                borderRadius: "1rem",
                maxWidth: "400px",
                backgroundColor: "#282f33",
              }}
            >
              <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
                <h4>WELCOME TO</h4>
                <h2 className="fw-bold mb-2 text-uppercase">ReactBites</h2>

                <p className="text-white-50 mb-5">
                  Sign up for a seamless experience
                </p>

                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Enter username"
                  name="username"
                  id="username"
                  type="text"
                  size="lg"
                  value={formik.values.username}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Enter name"
                  name="name"
                  id="name"
                  type="text"
                  size="lg"
                  value={formik.values.name}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Enter Email"
                  name="email"
                  id="email"
                  type="email"
                  size="lg"
                  value={formik.values.email}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Password?"
                  id="password"
                  name="password"
                  type="password"
                  size="lg"
                  value={formik.values.password}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Confirm Password?"
                  id="confirmpassword"
                  name="confirmpassword"
                  type="password"
                  size="lg"
                  value={formik.values.confirmpassword}
                  onChange={formik.handleChange}
                  style={{ color: "white" }}
                />

                <MDBBtn
                  outline
                  className="mt-2 mx-2 px-5"
                  color="white"
                  size="lg"
                  type="submit"
                  onClick={() => {
                    navigate("/");
                  }}
                >
                  SignUp
                </MDBBtn>

                <div>
                  <p className="mb-0 mt-4">
                    Already have an account?{" "}
                    <span
                      className="text-white-50 fw-bold"
                      style={{ cursor: "pointer" }}
                    >
                      Login
                    </span>
                  </p>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </form>
  );
}

function LoginPage() {
  const [signup, setSignUp] = useState(false);
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: signup ? signUpInitial : initial,
    onSubmit: (values) => {
      const formdata = {
        username: values.username,
        password: values.password,
        name: values.name, // Include name field
        usernameLogin: values.usernameLogin, // Include usernameLogin field
        passwordLogin: values.passwordLogin, // Include passwordLogin field
        // other fields...
      };
      console.log(formdata);
      formik.resetForm();
      navigate("/");
    },
  });

  useEffect(() => {
    return () => {
      formik.resetForm();
    };
  }, [signup]);

  return (
    <>
      <Header />
      {signup ? (
        <SignUpComponent formik={formik} />
      ) : (
        <LoginComponent formik={formik} />
      )}
    </>
  );
}

export default LoginPage;
