import React, { useState } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBIcon,
} from "mdb-react-ui-kit";
import "mdb-react-ui-kit/dist/css/mdb.min.css";
import { useFormik } from "formik";

const initial = {
  username: "",
  password: "",
};
function LoginComponent(props) {
  const formik = useFormik({
    initialValues: initial,
    onSubmit: (values) => {
      const formdata = {
        username: values.username,
        pasword: values.password,
      };
      console.log(formdata);
      formik.resetForm();
    },
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <MDBContainer style={{ fontFamily: "'Overpass', sans-serif" }} fluid>
        <MDBRow className="d-flex justify-content-center align-items-center h-100">
          <MDBCol col="12">
            <MDBCard
              className="bg-dark text-white my-5 mx-auto"
              style={{ borderRadius: "1rem", maxWidth: "400px" }}
            >
              <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
                <h4>WELCOME TO</h4>
                <h2 className="fw-bold mb-2 text-uppercase">ReactBites</h2>

                <p className="text-white-50 mb-5">
                  Login for a seamless experience
                </p>

                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Username"
                  name="username"
                  id="username"
                  type="text"
                  size="lg"
                  value={formik.values.username}
                  onChange={formik.handleChange}
                />
                <MDBInput
                  wrapperClass="mb-4 mx-5 w-100"
                  labelClass="text-white"
                  label="Password"
                  id="password"
                  name="password"
                  type="password"
                  size="lg"
                  value={formik.values.password}
                  onChange={formik.handleChange}
                />

                <MDBBtn
                  outline
                  className="mt-2 mx-2 px-5"
                  color="white"
                  size="lg"
                  type="submit"
                >
                  Login
                </MDBBtn>

                <div>
                  <p className="mb-0 mt-4">
                    Don't have an account?{" "}
                    <span
                      onClick={props.signupClick}
                      className="text-white-50 fw-bold"
                      style={{ cursor: "pointer" }}
                    >
                      Sign Up
                    </span>
                  </p>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </form>
  );
}

function LoginPage() {
  const [logIn, setLogIn] = useState(true);
  if (logIn)
    return (
      <LoginComponent
        signupClick={() => {
          setLogIn(false);
        }}
      />
    );
  return (
    <h1>
      "Welcome to ReactBites! We're delighted to have you join our culinary
      community. Your delicious journey is just a few minutes awayâ€”complete the
      signup form now to savor the convenience of ordering your favorite meals
      with us!
    </h1>
  );
}

export default LoginPage;
