import { useReducer, useEffect } from "react";
import CartContext from "./cart-context";

function cartReducer(state, action) {
  if (action.type === "add") {
    //here we need to check it action.item is in cart then only uodate its amount value
    //have to check in state.items then we'll put the map

    //check if action.item is in cart
    let flag = false;
    state.items.map((item) => {
      if (item.id === action.item.id) {
        console.log("match found");
        item.amount += action.item.amount;
        flag = true;
        return;
      }
    });

    if (!flag) {
      const updatedItems = state.items.concat(action.item);
      const newTotalAmount =
        state.totalAmount + action.item.price * action.item.amount;
      return {
        items: updatedItems,
        totalAmount: newTotalAmount,
      };
    }
  }
  return state;
}

const initial = {
  items: [],
  totalAmount: 0,
};

function CardProvider(props) {
  const [cartState, cartReducerFunction] = useReducer(cartReducer, initial);

  function addItemIntoCart(item) {
    cartReducerFunction({ type: "add", item: item });
  }
  function removeItemFromCart(id) {}

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemIntoCart,
    removeItem: removeItemFromCart,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
}

export default CardProvider;
