import classes from "./cssFiles/Input.module.css";


function Input(props) {
  return (
    <div className={classes.input}>
      <label htmlFor={props.input.id}>{props.label}</label>
      <input {...props.input} />
    </div>
  );
}

export default Input;

//we exoect the props contain the label:
// and also a input OBJECT
// and we wrote {...props.input} this means that when ever we get the object
// input would be containing the key value pair which will be autoimatically stored
//accordingly by react
// example input have {type: "text"} we can also do this with id
// it will do <input type="text" .. . ./>
